pragma solidity ^0.5.0;

contract PurchaseStuff {
  address payable public seller;
  uint public price;

  event PurchaseEvent(address indexed buyer, address indexed seller, uint price);

  constructor (address payable _seller, uint _price) public {
    seller = _seller;
    price = _price;
  }

  function purchase() public payable{

    //transfer ether to seller's address
    seller.transfer(msg.value);

    //emit event to indicate purchase
    emit PurchaseEvent(msg.sender, seller, price);
  }
}
